// import mysql from 'mysql';
const mysql = require('mysql');

export function initDB() {
    // Create connection
    const db = mysql.createConnection({
        host: 'localhost',
        user: 'root',
        password: '',
        database: 'node_erm'
    });

    db.connect((err) => {
        if(err) {
            console.error(err);
            return;
        }
        console.log('connected to mysql');
    });
    return { db };
}